    int vehiculo;
    int cliente;
    DateTime fecha_inicio = DateTime.Now;
    DateTime fecha_fin = DateTime.Now.AddDays(10);
    double monto;

    string message = "";

    List<Vehiculos> Vehicles() => new rentcarContext(options).Vehiculos.ToList();

    List<Clientes> Customers() => new rentcarContext(options).Clientes.ToList();

    void Process() {

        rentcarContext ctx = new rentcarContext(options);

        Alquileres newAlquiler = new Alquileres {

            VehiculosIdVehiculos = vehiculo,
            ClientesIdClientes = cliente,
            FechaInicio = fecha_inicio,
            FechaFin = fecha_fin,
            Monto = monto
        };

        try
        {
            ctx.Alquileres.Add(newAlquiler);
        }
        catch (System.Exception ex)
        {
             Console.WriteLine(ex.Message);
             message = "No se pudo realizar el registro del alquiler";
        }

    }


