    void Change(int id)
    {

        var customer = context.Vehiculos.Where(i => i.IdVehiculos == id).SingleOrDefault();

        if (customer.Status == 1)
        {

            customer.Status = 0;
        }
        else if (customer.Status == 0)
        {

            customer.Status = 1;
        }

        context.SaveChanges();
    }

